document.addEventListener("DOMContentLoaded",function(){let links=document.querySelectorAll(".aside__link"),sections=document.querySelectorAll(".info__section");links.forEach(link=>{link.addEventListener("click",function(){sections.forEach(section=>{section.classList.add("hidden")});var targetId=this.getAttribute("data-target");document.getElementById(targetId).classList.remove("hidden"),links.forEach(link=>link.classList.remove("aside__link--active")),this.classList.add("aside__link--active")})}),document.querySelectorAll(".accordeon__title").forEach(title=>{title.addEventListener("click",function(){let info=this.nextElementSibling;var isHidden=info.classList.contains("hidden");let icon=this.querySelector("img"),item=this.parentElement;isHidden?(info.classList.remove("hidden"),info.style.maxHeight=info.scrollHeight+"px",icon.classList.remove("rotate-45"),icon.classList.add("rotate-0"),item.classList.remove("terms__item"),item.classList.add("terms__item--active")):(info.style.maxHeight="0",info.addEventListener("transitionend",()=>{info.classList.add("hidden"),icon.classList.remove("rotate-0"),icon.classList.add("rotate-45"),item.classList.remove("terms__item--active"),item.classList.add("terms__item")},{once:!0}))})});let button=document.querySelector(".show-more-btn"),content=document.querySelector(".about");button.addEventListener("click",function(){content.classList.toggle("expanded"),content.classList.contains("expanded")?content.classList.add("no-gradient"):content.classList.remove("no-gradient"),button.style.display="none"})});